pragma solidity >= 0.5.0 < 0.7.0;

contract SongRegistry {
    // define a song struct
    struct Song {
        address owner;  // note that the ordering of the struct has been speficically
        //done to save gas. Something I missed in tutorial 3 of cryptozombies */
        string title;
        string url;
        uint price; //test
    }

    Song[] public songs; // don't forget the access modifier!

    mapping (uint => address[]) buyers; // creating a mapping between unique ideas and all of the people who've bought the song.
    // creating this mapping based on the instruc was quite confusing, but code-wise this makes good sense.

    function register(string memory _title, string memory _url, uint _price) public returns (uint) {
        songs.push(Song(msg.sender, _title, _url, _price));
        buyers[songs.length - 1].push(msg.sender);
    }

    function totalNumSongs() public view returns (uint256) {
        return (songs.length);
    }

    function isBuyer(uint256 _songID) private view returns (bool) {
        bool isOwner = false;
        for (uint i = 0; i < buyers[_songID].length; i++) {
            if (buyers[_songID][i] == msg.sender){
                isOwner = true;
            }
        }
        return isOwner;
        // address[] storage songBuyers = buyers[_songID]; //apparently this is more efficient! I'm not quite sure why yet, but something about not needing to go back to the blockchain on each iteration and fetch the value. The idea is that the storage keyword somehow makes a local copy or something. The whole storage vs memory difference is pretty confusing because it doesn't seem to be consistent!
        // bool isOwner = false;
        // for (uint i; i < songBuyers.length; i++) {
        //     if (songBuyers[i] == msg.sender){
        //         isOwner = true;
        //     }
        // }
        // return isOwner;
    }

    function buySong(uint256 _songID) public payable {
        require (msg.value == songs[_songID].price, "soz");
        buyers[_songID].push(msg.sender);
        Song storage song = songs[_songID]; // I think this was just gratuitous
        // pay the original owner of the song (the first "buyer" of the song)
        address(uint160(song.owner)).transfer(msg.value);
        // if the original owner address is payable, then do this:
        // song.owner.transfer(msg.value);
    }
}
